import 'package:flutter/material.dart';import 'package:flutter_food_app/config/locator.dart';import 'package:flutter_food_app/config/router.gr.dart';import 'package:flutter_food_app/data/global_data.dart';import 'package:stacked_services/stacked_services.dart';Padding buildActions(BuildContext context) {  final NavigationService _navigationService = locator<NavigationService>();  return Padding(    padding: const EdgeInsets.symmetric(horizontal: 5),    child: InkWell(      onTap: () {        _navigationService.navigateTo(Routes.cartScreen);      },      child: Stack(        children: [          Icon(            Icons.shopping_cart_outlined,            size: 35,          ),          Positioned(            top: 0,            right: 0,            child: Container(              decoration: BoxDecoration(                  color: Colors.red, borderRadius: BorderRadius.circular(20)),              child: Padding(                padding: const EdgeInsets.symmetric(horizontal: 5),                child: Text(GlobalData.cartItems.length.toString(),                    style: Theme.of(context)                        .textTheme                        .bodyText2                        .copyWith(color: Colors.white,fontSize: 12)),              ),            ),          )        ],      ),    ),  );}