import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:flutter_food_app/data/global_data.dart';import 'package:flutter_food_app/data/home_data_model.dart';import 'package:flutter_food_app/domain/api_services.dart';import 'package:flutter_food_app/presentation/widgets/toast.dart';part 'home_bloc_state.dart';class HomeBlocCubit extends Cubit<HomeBlocState> {  HomeBlocCubit() : super(HomeBlocInitial());  ///menu list  getFoodMenu({Map<String, String> params}) async {    try {      emit(Loading());      var res = await ApiServices().getMenuList(params: params);      GlobalData.homeDataModel = homeDataModelFromMap(res.body)[0];      emit(HomeDataResponse(GlobalData.homeDataModel));    } catch (e) {      print(e);      showToast(msg: 'error');      emit(DataError(e));    }  }}