import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:flutter_food_app/domain/api_services.dart';import 'package:flutter_food_app/presentation/widgets/toast.dart';part 'login_bloc_state.dart';class LoginBlocCubit extends Cubit<LoginBlocState> {  LoginBlocCubit() : super(LoginBlocInitial());  ///user login  getLogin(Map<String,String> params) async {    try {      emit(Loading());      //var res = await ApiServices().getLogin(params: params);      // emit(LoginDataResponse(res.data));      emit(LoginDataResponse(''));    } catch (e) {      showToast(msg:'user not found');      emit(DataError(e));    }  }}