import 'dart:async';import 'package:flutter/material.dart';import 'package:flutter_food_app/config/locator.dart';import 'package:flutter_food_app/config/router.gr.dart';import 'package:stacked_services/stacked_services.dart';class SplashScreen extends StatefulWidget {  @override  _SplashScreenState createState() => _SplashScreenState();}class _SplashScreenState extends State<SplashScreen> {  final NavigationService _navigationService = locator<NavigationService>();  @override  void initState() {    super.initState();    Timer(      Duration(seconds: 3),      () => _navigationService.pushNamedAndRemoveUntil(Routes.loginScreen),    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.white,      body: Center(        child: Text(          'Flutter App',          style: Theme.of(context).textTheme.headline5,        ),      ),    );  }}